import Link from 'next/link';
import { Button } from 'antd';
import Router from 'next/router';
import { Fragment } from 'react';
import { NextComponentType } from 'next';

const events = [
    'routeChangeStart',
    'routeChangeComplete',
    'routeChangeError',
    'beforeHistoryChange',
    'hashChangeStart',
    'hashChangeComplete',
];

function makeEvent(type: string) {
    return (...args: any) => {
        console.log(type, ...args);
    }
}

events.forEach((event: string) => {
    Router.events.on(event, makeEvent(event));
})


interface IndexProps {
    name: string
}

// NextComponentType接收的三种类型分别为： baseContext，InitialProps，Props(从props获取)
const Index: NextComponentType<any, IndexProps, any> = (props) => {

    console.log(props.name);


    function goToHome() {
        Router.push({
            pathname: '/home',
            query: {
                id: 2
            }
        }, '/home/2');
    }

    return (
        <Fragment>
            <Link href='/home?id=10' as='/home/10'>
                <Button>你好</Button>
            </Link>
            <Button onClick={goToHome}>goTo Home</Button>
        </Fragment>
    )
}

Index.getInitialProps = () => {
    return {
        name: 'CregskiN'
    }
}


export default Index;
